    // powersofnumbers logic function
    public float powersofnumbers(float x, float y){
        float hasil=1;
        
        if(y<0){
            y=y*-1;
               for (int i = 1; i <=y;i++){
            // multiply the x with itself
            hasil *= x;
               }
               hasil = 1/hasil;
        }
        else{
        // loop as much as the y values (the powers)
        for (int i = 1; i <=y;i++){
            // multiply the x with itself
            hasil *= x;
        }
        }
        
        // return the x values after final loop
        return hasil;
    }
    
